import React, { useEffect, useState } from 'react';

function ManagerMetrics() {
  const [employeesBadges, setEmployeesBadges] = useState([]);
  const [trainingAssignments, setTrainingAssignments] = useState([]);
  const [loading, setLoading] = useState(true);

  // Assuming user information is stored in local storage
  const user = JSON.parse(localStorage.getItem('user'));

  useEffect(() => {
    const fetchEmployeeBadges = async () => {
      try {
        const response = await fetch(`http://localhost:4000/badges/organization/${user.organization_id}`);
        const data = await response.json();
        setEmployeesBadges(data);
      } catch (error) {
        console.error('Error fetching employee badges:', error);
      }
    };

    const fetchTrainingAssignments = async () => {
      try {
        const response = await fetch(`http://localhost:4000/training-assignments/${user.organization_id}`);
        const data = await response.json();
        setTrainingAssignments(data);
      } catch (error) {
        console.error('Error fetching training assignments:', error);
      }
    };

    fetchEmployeeBadges();
    fetchTrainingAssignments();
    setLoading(false);
  }, [user.organization_id]);

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>Manager Metrics Dashboard</h1>
      <div>
        <h2>Employee Badges</h2>
        {/* Render employee badges */}
        {employeesBadges.map((employee) => (
          <div key={employee.id}>
            <h3>{employee.name}</h3>
            <ul>
              {employee.badges.map((badge) => (
                <li key={badge.id}>{badge.name}</li>
              ))}
            </ul>
          </div>
        ))}
      </div>
      <div>
        <h2>Training Assignments</h2>
        {/* Render training assignments */}
        {trainingAssignments.map((assignment) => (
          <div key={assignment.id}>
            <h3>{assignment.employeeName}</h3>
            <ul>
              {assignment.trainings.map((training) => (
                <li key={training.id}>{training.name}</li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ManagerMetrics;
