import React, { useState, useEffect } from 'react';

function UserHome() {
  const user = JSON.parse(localStorage.getItem('user')); // Retrieve the user object from local storage

  const [employees, setEmployees] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchEmployees = async () => {
      try {
        const response = await fetch(`http://localhost:4000/users?organization_id=${user.organization_id}`);
        const data = await response.json();
        setEmployees(data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching employees:', error);
        setLoading(false);
      }
    };

    if (user.user_role === 'manager') {
      fetchEmployees();
    } else {
      setLoading(false);
    }
  }, [user.organization_id, user.user_role]);

  if (loading) {
    return <p>Loading employees...</p>;
  }

  return (
    <div>
      <h2>Welcome, {user.username}!</h2>
      <p>Email: {user.email}</p>
      <p>User ID: {user.user_id}</p>
      <p>First Name: {user.first_name}</p>
      <p>Last Name: {user.last_name}</p>
      <p>Organization ID: {user.organization_id}</p>
      <p>User Role: {user.user_role}</p>

      {user.user_role === 'manager' && (
        <div>
          <h3>Employee List:</h3>
          <ul>
            {employees.map(employee => (
              <li key={employee.user_id}>
                <p>Name: {employee.username}</p>
                <p>Email: {employee.email}</p>
                <p>User ID: {employee.user_id}</p>
                <p>First Name: {employee.first_name}</p>
                <p>Last Name: {employee.last_name}</p>

                <h4>Badge Collection:</h4>
                <ul>
                  {employee.badges.map(badge => (
                    <li key={badge.badge_id}>
                      <img
                        src={process.env.PUBLIC_URL + '/badges/' + badge.image}
                        alt={badge.name}
                        style={{ width: '100px', height: '100px' }}
                      />
                      {badge.badge_id} - {badge.name}
                    </li>
                  ))}
                </ul>
              </li>
            ))}
          </ul>
        </div>
      )}

      <h3>Your Badges:</h3>
      <ul>
        {user.badges.map(badge => (
          <li key={badge.badge_id}>
            <img
              src={process.env.PUBLIC_URL + '/badges/' + badge.image}
              alt={badge.name}
              style={{ width: '100px', height: '100px' }}
            />
            {badge.badge_id} - {badge.name}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default UserHome;
