// MyChartComponent.js
import React from 'react';
import { Bar } from 'react-chartjs-2';
import 'chart.js/auto';

const MyChartComponent = ({ employeeTrainingData }) => {
  // Assuming employeeTrainingData is an array of objects like:
  // [{ name: "Employee 1", assigned: 5, completed: 3 }, ...]

  const data = {
    labels: employeeTrainingData.map(item => item.name),
    datasets: [
      {
        label: 'Trainings Assigned',
        backgroundColor: 'rgba(255,99,132,0.2)',
        borderColor: 'rgba(255,99,132,1)',
        borderWidth: 1,
        data: employeeTrainingData.map(item => item.assigned),
      },
      {
        label: 'Trainings Completed',
        backgroundColor: 'rgba(54, 162, 235, 0.2)',
        borderColor: 'rgba(54, 162, 235, 1)',
        borderWidth: 1,
        data: employeeTrainingData.map(item => item.completed),
      }
    ]
  };

  const options = {
    scales: {
      y: {
        beginAtZero: true,
      }
    }
  };

  return <Bar data={data} options={options} />;
};

export default MyChartComponent;
