import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

function ManagerMetrics() {
  const [employeesBadges, setEmployeesBadges] = useState([]);
  const [employeesTrainings, setEmployeesTrainings] = useState([]);

  useEffect(() => {
    fetchEmployeesBadges();
    fetchEmployeesTrainings();
  }, []);

  async function fetchEmployeesBadges() {
    // Assuming an endpoint exists at /api/employees-badges that returns badges for all employees under a manager
    const response = await fetch('/api/employees-badges');
    const data = await response.json();
    setEmployeesBadges(data);
  }

  async function fetchEmployeesTrainings() {
    // Assuming an endpoint exists at /api/employees-trainings that returns training info for all employees under a manager
    const response = await fetch('/api/employees-trainings');
    const data = await response.json();
    setEmployeesTrainings(data);
  }

  return (
    <div>
      <h1>Manager Metrics Dashboard</h1>
      <div>
        <h2>Employee Badges</h2>
        {/* Render employee badges */}
        {employeesBadges.map((employee) => (
          <div key={employee.id}>
            <h3>{employee.name}</h3>
            <ul>
              {employee.badges.map((badge) => (
                <li key={badge.id}>{badge.name}</li>
              ))}
            </ul>
          </div>
        ))}
      </div>
      <div>
        <h2>Employee Trainings</h2>
        {/* Render employee trainings */}
        {employeesTrainings.map((employee) => (
          <div key={employee.id}>
            <h3>{employee.name}</h3>
            <ul>
              {employee.trainings.map((training) => (
                <li key={training.id}>{training.name}</li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ManagerMetrics;
