import React, { useState, useEffect } from 'react';

function UserHome() {
  const user = JSON.parse(localStorage.getItem('user')); // Retrieve the user object from local storage

  const [badges, setBadges] = useState([]);

  useEffect(() => {
    const fetchBadges = async () => {
      try {
        const response = await fetch(`http://localhost:4000/badges?user_id=${user.id}`);
        const data = await response.json();
        setBadges(data);
      } catch (error) {
        console.error('Error fetching badges:', error);
      }
    };

    fetchBadges();
  }, [user.id]);

  return (
    <div>
      <h2>Welcome, {user.username}!</h2>
      <p>Email: {user.email}</p>
      <p>User ID: {user.user_id}</p>
      <p>First Name: {user.first_name}</p>
      <p>Last Name: {user.last_name}</p>
      <p>Organization ID: {user.organization_id}</p>
      <p>User Role: {user.user_role}</p>

      <h3>Badges:</h3>
      <ul>
        {badges && badges.length > 0 ? (
          (() => {
            const items = [];
            for (const badge of badges) {
              items.push(<li key={badge.badges_id}>{badge.badge_name}</li>);
            }
            return items;
          })()
        ) : (
          <li>No badges found</li>
        )}
      </ul>
    </div>
  );
}

export default UserHome;
