import React from 'react';

function UserHome() {
  const user = JSON.parse(localStorage.getItem('user')); // Retrieve the user object from local storage

  const [badges, setBadges] = useState([]);

  useEffect(() => {
    // Make an API call to fetch the badges associated with the user
    fetch(`/api/badges?user_id=${user.user_id}`)
      .then(response => response.json())
      .then(data => setBadges(data));
  }, [user.user_id]);

  return (
    <div>
      <h2>Welcome, {user.username}!</h2>
      <p>Email: {user.email}</p>
      <p>First Name: {user.first_name}</p>
      <p>Last Name: {user.last_name}</p>
      <p>Organization ID: {user.organization_id}</p>
      <p>User Role: {user.user_role}</p>

      <h3>Badges:</h3>
      <ul>
        {badges.map(badge => <li key={badge.badge_id}>{badge.badge_name}</li>)}
      </ul>
    </div>
  );
}

export default UserHome;
