import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

function UserHome() {
  const user = JSON.parse(localStorage.getItem('user'));

  const [badges, setBadges] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchBadgesAndEmployees = async () => {
      try {
        // Fetch badges
        const badgesResponse = await fetch(`http://localhost:4000/badges?user_id=${user.user_id}`);
        const badgesData = await badgesResponse.json();
        setBadges(badgesData);
  
        // Fetch employees
        if (user.user_role === 'management') {
          const employeesResponse = await fetch(`http://localhost:4000/employees?organization_id=${user.organization_id}`);
          const employeesData = await employeesResponse.json();
          setEmployees(employeesData);
        }
  
        setLoading(false); // Mark loading as false once badges and employees are fetched
      } catch (error) {
        console.error('Error fetching data:', error);
        setLoading(false); // Mark loading as false in case of error
      }
    };
  
    fetchBadgesAndEmployees();
  }, [user.id]);

  if (loading) {
    return <p>Loading badges and employees...</p>;
  }

  return (
    <div>

      <Link to='/TrainingModule' className='btn btn-default border w-15 bg-light rounded 0 text-decoration-none'>Training Module Page</Link>


      <h2>Welcome, {user.username}!</h2>
      <p>Email: {user.email}</p>
      <p>User ID: {user.user_id}</p>
      <p>First Name: {user.first_name}</p>
      <p>Last Name: {user.last_name}</p>
      <p>Organization ID: {user.organization_id}</p>
      <p>User Role: {user.user_role}</p>

      <h3>Your Badges:</h3>
      <ul>
        {badges.map(badge => (
          <li key={badge.badge_id}>
            {badge.badge_id === 1 && (
              <>
                <img src={process.env.PUBLIC_URL + '/gold_badge.jpg'} alt="Gold Badge" style={{ width: '100px', height: '100px' }} />
                {badge.badge_id} - {badge.badge_name}
              </>
            )}
            {badge.badge_id === 2 && (
              <>
                <img src={process.env.PUBLIC_URL + '/silver_badge.jpg'} alt="Silver Badge" style={{ width: '100px', height: '100px' }} />
                {badge.badge_id} - {badge.badge_name}
              </>
            )}
            {badge.badge_id === 3 && (
              <>
                <img src={process.env.PUBLIC_URL + '/bronze_badge.jpg'} alt="Bronze Badge" style={{ width: '100px', height: '100px' }} />
                {badge.badge_id} - {badge.badge_name}
              </>
            )}
          </li>
        ))}
      </ul>
      {user.user_role === 'management' && (
        <div className='bg-info p-3 rounded w-25'>
          <div>
            <h3>Employees in Your Organization:</h3>
            <ul>
              {employees.map(employee => {
                // Filter badges for the current employee
                const employeeBadges = badges.filter(badge => badge.user_id === employee.user_id);

                // Log the badges for debugging
                console.log("Badges for user ID", employee.user_id, ":", employeeBadges);

                return (
                  <li key={employee.user_id}>
                    <p>First Name: {employee.first_name}</p>
                    <p>Last Name: {employee.last_name}</p>
                    <p>Email: {employee.email}</p>
                    <p>User ID: {employee.user_id}</p>
                    <ul>
                      {employeeBadges.map(badge => (
                        <li key={badge.badge_id}>
                          {badge.badge_id === 1 && (
                            <>
                              <img src={process.env.PUBLIC_URL + '/gold_badge.jpg'} alt="Gold Badge" style={{ width: '100px', height: '100px' }} />
                              {badge.badge_id} - {badge.badge_name}
                            </>
                          )}
                          {badge.badge_id === 2 && (
                            <>
                              <img src={process.env.PUBLIC_URL + '/silver_badge.jpg'} alt="Silver Badge" style={{ width: '100px', height: '100px' }} />
                              {badge.badge_id} - {badge.badge_name}
                            </>
                          )}
                          {badge.badge_id === 3 && (
                            <>
                              <img src={process.env.PUBLIC_URL + '/bronze_badge.jpg'} alt="Bronze Badge" style={{ width: '100px', height: '100px' }} />
                              {badge.badge_id} - {badge.badge_name}
                            </>
                          )}
                        </li>
                      ))}
                    </ul>
                  </li>
                );
              })}
            </ul>

          </div>
        </div>
      )}
    </div>
  );
}

export default UserHome;
