// Import React
import React from 'react';
// Import the chart type
import { Bar } from 'react-chartjs-2';
// Import Chart.js to ensure it's registered
import 'chart.js/auto';

const EmployeeTimeBarChart = ({ employeeData }) => {
  // Extract employee names and average completion times from data
  const employeeNames = Object.keys(employeeData);
  const averageTimes = Object.values(employeeData);
  
  // Calculate average completion time across all employees
  const totalAverageTime = averageTimes.reduce((acc, time) => acc + time, 0) / averageTimes.length;
  
  // Data for the bar chart
  const chartData = {
    labels: employeeNames,
    datasets: [
      {
        label: 'Employee Completion Time',
        data: averageTimes,
        backgroundColor: 'rgba(54, 162, 235, 0.6)',
      },
      {
        label: 'Average Completion Time',
        data: Array(employeeNames.length).fill(totalAverageTime),
        backgroundColor: 'rgba(255, 99, 132, 0.6)',
      },
    ],
  };

  return (
    <div>
      <h3>Comparison of Employee Completion Time with Average Completion Time</h3>
      <Bar data={chartData} />
    </div>
  );
};

export default EmployeeTimeBarChart;
