// Import React and necessary Chart.js components
import React from 'react';
import { Pie } from 'react-chartjs-2';
import 'chart.js/auto';

const TrainingOverviewPieChart = ({ completeCount, assignedCount }) => {
  const data = {
    labels: ['Completed Trainings', 'Assigned Trainings'],
    datasets: [
      {
        label: 'Training Overview',
        backgroundColor: ['#36A2EB', '#FF6384'],
        hoverBackgroundColor: ['#36A2EBbb', '#FF6384bb'],
        data: [completeCount, assignedCount],
      },
    ],
  };

  const options = {
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: 'Total Training Overview',
      },
    },
  };

  return (
    <div style={{ height: '300px', width: '100%' }}>
      <Pie data={data} options={options} />
    </div>
  );
};

export default TrainingOverviewPieChart;
