import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function StartModule() {
    const { moduleId } = useParams();
    const [moduleContent, setModuleContent] = useState(null);

    const [duration, setDuration] = useState(null);

    useEffect(() => {
        fetchModuleContent(moduleId);
    }, [moduleId]);
 
    const fetchModuleContent = async (moduleId) => {
        try {
            const response = await fetch();
            const data = await response.json();
            setModuleContent(data);
        } catch (error) {
            console.error('Error fetching module content:', error);
        }
    };

    const handleEndModule = () => {
        const endTime = new Date().getTime();
        const startTime = localStorage.getItem('trainingStartTime');

        if(startTime) {
            const moduleDuration = (endTime - parseInt(startTime)) / 1000; // Duration in seconds
            setDuration(moduleDuration);
        }else {
            console.error('Training start time not found in local storage');
        }
    };

    return (
        <div style={{ padding: '20px' }}>
            <h2>Training Module</h2>
            
        </div>

    );


}

export default StartModule;
