import React, { useState, useEffect } from 'react';

function TrainingModulesPage() {
  const [assignedModules, setAssignedModules] = useState([]);
  const [completedModules, setCompletedModules] = useState([]);
  const [allTrainings, setAllTrainings] = useState([]);
  const [trainingAssignments, setTrainingAssignments] = useState([]);
  const [user, setUser] = useState(JSON.parse(localStorage.getItem('user')));
  const [selectedEmployee, setSelectedEmployee] = useState('');
  const [selectedModule, setSelectedModule] = useState('');
  const [employees, setEmployees] = useState([]);
  const [selectedUnenrollEmployee, setSelectedUnenrollEmployee] = useState('');
  const [unenrollModules, setUnenrollModules] = useState([]); // New state for unenrollModules
  const [loading, setLoading] = useState(true);

  // Fetch all trainings
  useEffect(() => {
    fetchAllTrainings();
  }, []);

  // Fetch training assignments and employees if user role is management
  useEffect(() => {
    if (user && user.user_role === 'management') {
      fetchTrainingAssignments();
      fetchEmployees();
    }
  }, [user]);

  // Fetch training modules when user ID changes
  useEffect(() => {
    if (user && user.user_id) {
      fetchTrainingModules();
    }
  }, [user.user_id]);

  // Fetch all trainings
  const fetchAllTrainings = async () => {
    const response = await fetch('http://localhost:4000/all-trainings');
    const data = await response.json();
    setAllTrainings(data);
  };

  // Fetch training assignments
  const fetchTrainingAssignments = async () => {
    const response = await fetch(`http://localhost:4000/training-assignments/${user.organization_id}`);
    const data = await response.json();
    setTrainingAssignments(data);
  };

  // Fetch training modules
  const fetchTrainingModules = async () => {
    const response = await fetch(`http://localhost:4000/user-training-modules?userId=${user.user_id}`);
    const data = await response.json();
    setAssignedModules(data.assignedModules);
    setCompletedModules(data.completedModules);
  };

  // Fetch employees
  const fetchEmployees = async () => {
    const url = `http://localhost:4000/employees/organization/${user.organization_id}`;
    const response = await fetch(url);
    const data = await response.json();
    setEmployees(data);
  };

  // Fetch unenroll modules when selectedUnenrollEmployee changes
  useEffect(() => {
    if (selectedUnenrollEmployee) {
      fetchUnenrollModules(selectedUnenrollEmployee);
    }
  }, [selectedUnenrollEmployee]);

  // Fetch unenroll modules
  const fetchUnenrollModules = async (employeeId) => {
    const response = await fetch(`http://localhost:4000/unenroll-modules/${employeeId}`);
    const data = await response.json();
    setUnenrollModules(data);
  };

  // Function to handle unenrollment
  const unenrollEmployeeFromTraining = async () => {
    // Add your unenrollment logic here
  };

  return (
    <div style={{ padding: '20px' }}>
      {/* Your JSX code here */}
      {/* Dropdown for selecting employee */}
      <select value={selectedUnenrollEmployee} onChange={e => setSelectedUnenrollEmployee(e.target.value)}>
        <option value="">Select Employee</option>
        {employees.map(employee => (
          <option key={employee.user_id} value={employee.user_id}>
            {employee.first_name} {employee.last_name}
          </option>
        ))}
      </select>
      {/* Dropdown for selecting module to unenroll from */}
      <select value={selectedModule} onChange={e => setSelectedModule(e.target.value)}>
        <option value="">Select Module</option>
        {unenrollModules.map(module => (
          <option key={module.module_id} value={module.module_id}>
            {module.module_name}
          </option>
        ))}
      </select>
      {/* Button to unenroll */}
      <button onClick={unenrollEmployeeFromTraining}>Unenroll</button>
    </div>
  );
}

export default TrainingModulesPage;
