import React, { useState, useEffect } from 'react';

const TrainingModuleContent = ({ moduleId, userId }) => {

  const [startTime, setStartTime] = useState(null);
  const [endTime, setEndTime] = useState(null);
  const [moduleContent, setModuleContent] = useState(null);

  useEffect(() => {
    const fetchModuleContent = async () => {
      try {
        const response = await fetch(`http://localhost:4000/module-content/${moduleId}`);
        const data = await response.json();
        if (response.ok) {
          setModuleContent(data);
        } else {
          console.error('Failed to fetch module content:', data.error);
        }
      } catch (error) {
        console.error('Error fetching module content:', error);
      }
    };

    fetchModuleContent();

    // Send a request to update start time in the backend when the component mounts
    const updateStartTime = async () => {
      try {
        const response = await fetch('http://localhost:4000/update-training-start-time', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ userId, moduleId }),
        });
        const data = await response.json();
        if (data.success) {
          setStartTime(new Date());
        } else {
          console.error('Failed to update start time:', data.error);
        }
      } catch (error) {
        console.error('Error updating start time:', error);
      }
    };

    updateStartTime();

    // Cleanup function to update end time when the component unmounts
    return () => {
      if (startTime) {
        setEndTime(new Date());
        // Send a request to update end time in the backend
        updateEndTime();
      }
    };
  }, [moduleId]);

  const updateEndTime = async () => {
    try {
      const response = await fetch('http://localhost:4000/update-training-end-time', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ userId, moduleId }),
      });
      const data = await response.json();
      if (data.success) {
        console.log('End time updated successfully.');
      } else {
        console.error('Failed to update end time:', data.error);
      }
    } catch (error) {
      console.error('Error updating end time:', error);
    }
  };

  const handleEndTraining = () => {
    // Set end time when the "End Training" button is clicked
    setEndTime(new Date());
    // Update end time in the backend
    updateEndTime();
  };

  return (
      <div>
        {moduleContent ? (
          <div style={{ padding: '20px' }}>
            <h2 style={{ fontSize: '20px' }}>{moduleContent.module_name}</h2>
            <iframe src={moduleContent.module_link} width="100%" height="400" title={moduleContent.module_type} style={{ border: 'none', marginBottom: '10px' }}></iframe>
            {moduleContent.module_format === 'slides' && (
              <button onClick={handleEndTraining} style={{ backgroundColor: 'green', color: 'white', borderRadius: '5px', padding: '10px 20px', cursor: 'pointer', border: 'none', width: '100%' }}>
                I attest to completing this training
              </button>
            )}
          </div>
        ) : (
          <p>Loading module content...</p>
        )}
      </div>
  );
};

export default TrainingModuleContent;
