import React, { useState, useEffect } from 'react';



function UserHome() {
  const user = JSON.parse(localStorage.getItem('user')); // Retrieve the user object from local storage

  const [badges, setBadges] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchBadges = async () => {
      try {
        const response = await fetch(`http://localhost:4000/badges?user_id=${user.user_id}`);
        const data = await response.json();
        setBadges(data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching badges:', error);
        setLoading(false);
      }

      const fetchEmployees = async () => {
        try {
          const response = await fetch(`http://localhost:4000/employees?organization_id=${user.organization_id}`);
          const data = await response.json();
          setEmployees(data);
        } catch (error) {
          console.error('Error fetching employees:', error);
        }
      };
  
      if (user.user_role === 'management') {
        fetchEmployees();
      }
    };

  

    fetchBadges();
  }, [user.id]);

  if (loading) {
    return <p>Loading badges...</p>;
  }

  return (
    <div style={{ fontFamily: 'Arial, sans-serif', maxWidth: '800px', margin: '0 auto', padding: '20px', backgroundColor: '#f9f9f9', borderRadius: '8px', boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)' }}>
      <h2 style={{ marginBottom: '20px' }}>Welcome, {user.username}!</h2>
      <p><strong>Email:</strong> {user.email}</p>
      <p><strong>User ID:</strong> {user.user_id}</p>
      <p><strong>First Name:</strong> {user.first_name}</p>
      <p><strong>Last Name:</strong> {user.last_name}</p>
      <p><strong>Organization ID:</strong> {user.organization_id}</p>
      <p><strong>User Role:</strong> {user.user_role}</p>
  
      <h3 style={{ marginTop: '40px', marginBottom: '20px' }}>Your Badges:</h3>
      <ul>
        {badges.map(badge => (
          <li key={badge.badge_id} style={{ marginBottom: '10px', listStyle: 'none' }}>
            <img src={`${process.env.PUBLIC_URL}/${badge.image}`} alt={badge.name} style={{ width: '100px', height: '100px', marginRight: '10px', verticalAlign: 'middle' }} />
            <span style={{ verticalAlign: 'middle' }}>{badge.badge_id} - {badge.badge_name}</span>
          </li>
        ))}
      </ul>
  
      {user.user_role === 'management' && (
        <div>
          <h3 style={{ marginTop: '40px', marginBottom: '20px' }}>Employees in Your Organization:</h3>
          <ul>
            {employees.map(employee => (
              <li key={employee.user_id} style={{ marginBottom: '20px', listStyle: 'none' }}>
                <p><strong>First Name:</strong> {employee.first_name}</p>
                <p><strong>Last Name:</strong> {employee.last_name}</p>
                <p><strong>Email:</strong> {employee.email}</p>
                <p><strong>User ID:</strong> {employee.user_id}</p>
                <ul>
                  {badges
                    .filter(badge => badge.user_id === employee.user_id)
                    .map(badge => (
                      <li key={badge.badge_id} style={{ marginBottom: '10px', listStyle: 'none' }}>
                        <img src={`${process.env.PUBLIC_URL}/${badge.image}`} alt={badge.name} style={{ width: '100px', height: '100px', marginRight: '10px', verticalAlign: 'middle' }} />
                        <span style={{ verticalAlign: 'middle' }}>{badge.badge_id} - {badge.badge_name}</span>
                      </li>
                    ))}
                </ul>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default UserHome;
